css:
.date-table{
    line-height: 5px;
    font-size: 12px;
    font-weight: 500;
    width: 100%;
    border-collapse: collapse;
}
.date-table td{
    padding: 0px;
    margin: 0px;
    text-align: left;
}

.body {
    width:100%;
    font-size:15px;
    font-weight:500;
    font-family: 'Open Sans', sans-serif;
    padding:0 30px;
}

.print-format {
    font-family: 'Open Sans', sans-serif;
    margin:0;
}
.my-table {
    line-height: 1px;
    font-size: 12px;
    font-weight: 500;
    width: 100%;
    border-collapse: collapse;
}

.my-table th,
.my-table td {
    border: 1px solid black;
    padding: 0px;
    margin: 0px;
    text-align: left;
}

.my-table tr {
    padding: 0px;
    margin: 0px;
}

.my-table th,
.my-table thead {
    font-size: 12px;
    font-weight: 800;
}

.flex-row {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
}
.flex-col {
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
.text-left {
    text-align: left;
}
.text-right {
    text-align: right;
}
.total{
    font-size: 18px;
    font-weight: 700;
    margin:30px 0;
}
.footer {
    width:100%;
    position: absolute;
    bottom: 20px;
    left:0;
    font-size:10px;
    font-weight:500;
}

