    table {
        line-height: 1px;
        font-size: 10px;
        font-weight: 700;
        width: 100%;
        border-collapse: collapse;
    }
    th,
    td {
        border: 1px solid black;
        padding: 0px;
        margin: 0px;
        text-align: left;
    }
    tr {
        padding: 0px;
        margin: 0px;
    }
    th,
    thead {
        font-size: 12px;
        font-weight: 800;
    }
    .flex-row {
        width: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    .flex-col {
        width: 100%;
        display: flex;
        flex-direction: column;
