        text-align: left;
    }
    .text-right {
        text-align: right;
    }
</style>

<div class="flex-row" style="justify-content: end">
    <div class="flex-col" style="width: fit-content">
        <div class="text-right">Date Issued: {{ doc.date_issue}}</div>
        <div class="text-right">Ref: {{ doc.ref or doc.name}}</div>
    </div>
</div>

<table>
    <tbody>
        <tr>
            <td>Company</td>
            <td>{{ doc.company }}</td>
        </tr>
        <tr>
            <td>Rider Name</td>
            <td>{{ doc.rider_name }}</td>
        </tr>
        <tr>
            <td>Rider ID</td>
            <td>{{ doc.rider_id }}</td>
        </tr>
        <tr>
            <td>Rider CPR</td>
            <td>{{ doc.rider_cpr }}</td>
        </tr>
    </tbody>
</table>

<div class="flex-row">
    <div class="flex-col">
        <div class="flex-row" style="justify-content: space-between">
            <div>Payroll Month:</div>
            <div class="text-right">{{ doc.payroll_month }}</div>
        </div>
        <div class="flex-row" style="justify-content: space-between">
            <div>Start Date:</div>
            <div class="text-right">
                {{ doc.start_date }}
            </div>
        </div>
        <div class="flex-row" style="justify-content: space-between">
            <div class="text-left">End Date:</div>
            <div class="text-right">
                {{ doc.end_date }}
            </div>
        </div>
    </div>
    <div class="flex-col" style="align-items: end">
        <div class="flex-row" style="justify-content: space-between">
            <div>Payment Mode:</div>
            <div class="text-right">{{ doc.mode_of_payment }}</div>
        </div>
    </div>
</div>
<div class="flex-row">
    <h3 style="text-decoration: underline">Particulars</h3>
</div>

<table>
    <thead>
        <tr>
            <td>Sr.</td>
            <td>Components</td>
            <td style="text-align: center">Amount</td>
        </tr>
    </thead>
    <tbody>
        {% for item in doc.salary_components %}
        <tr>
            <td>{{ loop.index }}</td>
            <td>{{ item.component_name }}</td>
            <td style="text-align: center">
                {{ '%0.3f'|format(item.amount|float) }}
            </td>
        </tr>
        {% endfor %}
    </tbody>
</table>
"}]
